INSERT INTO `Programs` VALUES (1,1,'Program to Print \"Hello world\" ','Simple verilog program which describs basic structure and syntax of module in Verilog','module Hello_World;\r\ninitial begin\r\n$display(\"Hello World By DNS\");\r\n#10 $finish;\r\nend \r\nendmodule'),(2,1,'Asynchronous reset D-Flip Flop','This program describes the implementation of Asynchronous D- FF. The output will only change on the falling edge or trailing edge of the applied clk input.','module dff_async(clk,reset,q);         \r\n\r\n //-----------Input Ports---------------\r\ninput clk, reset ; \r\n  \r\n //-----------Output Ports---------------\r\n output [7:0] q;\r\n reg [7:0] data;\r\n //------------Internal Variables--------\r\n reg [7:0] q;\r\n initial\r\n begin\r\n data=8\'b11100111;\r\n end\r\n //-------------Code Starts Here---------\r\n always @ ( posedge clk or negedge reset)\r\n if (~reset) begin\r\n q <= 1\'b0;\r\n end  else begin\r\n q <= data;\r\n end \r\n endmodule //End Of Module dff_async_reset'),(3,1,'Synchronous reset D- Flip Flop','This program describes the implementation of Synchronous reset D-Flip Flop. On the positive edge of clock, if the value of reset is true then the data will be loaded to the output, else the output will be zero.','module dff_sync_reset (clk,reset,q);        \r\n\r\n//-----------Input Ports---------------\r\ninput clk, reset ; \r\n//-----------Output Ports---------------\r\noutput [7:0] q;\r\n//------------Internal Variables--------\r\nreg [7:0] q;\r\nreg [7:0] data;\r\ninitial\r\nbegin\r\ndata=8\'b11100001;\r\nend\r\n//-------------Code Starts Here---------\r\nalways @ ( posedge clk)\r\nif (~reset) begin\r\n  q <= 1\'b0;\r\nend  else begin\r\n  q <= data;\r\nend\r\n \r\nendmodule //End Of Module dff_sync_reset\r\n'),(4,1,'D Latch','This verilog program describes the Regular D Latch implementation in which the true value of enable (en) input directs the data to the output (q).','module dff_async(clk,q);        \r\n\r\n //-----------Input Ports---------------\r\ninput clk; \r\n  \r\n //-----------Output Ports---------------\r\n output [7:0] q;\r\n reg [7:0] data;\r\n //------------Internal Variables--------\r\n reg [7:0] q;\r\n initial\r\n begin\r\n data=8\'b11100111;\r\n end\r\n //-------------Code Starts Here---------\r\n always @ ( posedge clk )\r\n q <= data;\r\n \r\n endmodule //End Of Module dff_async_reset'),(5,1,'8-Bit Simple Up Counter','Implementation of 8-Bit Simple Up Counter using if else statements. ','module up_counter(out,enable,clk,reset);     \r\n\r\n//----------Output Ports--------------\r\n   output [7:0] out;\r\n//------------Input Ports--------------\r\n  input enable, clk, reset;\r\n//------------Internal Variables--------\r\n     reg [7:0] out;\r\n//-------------Code Starts Here-------\r\ninitial\r\nbegin\r\nout=0;\r\nend\r\nalways @(posedge clk)\r\nif (reset) begin\r\nout <= 8\'b0 ;\r\nend else if (enable) begin\r\nout <= out + 1;\r\nend\r\nendmodule \r\n\r\n'),(6,2,'Program demonstrating the priority of Verilog Operators','This program demonstrate the priority of Verilog Operators which provide a means to construct expressions. Most of the operators are similar to that of C.','\r\nmodule priority_of_operators(clk,d,ans);\r\ninput clk;\r\noutput reg [15:0] d,ans;  \r\ninteger a,b,c;\r\nalways @(posedge clk)\r\nbegin\r\nd=5+10-3*2/6%3;//target expression\r\na=3*2;//first step\r\nb=a/6;//second step\r\nc=b%3;//third step\r\nans=5+10-c;//final answer\r\n//$display(\"d=%d  ans=%d\",d,ans);\r\nend\r\nendmodule\r\n\r\n//This list provides priority of Verilog Operators:\r\n// Operator	Type\r\n//////////////////////////////////////////\r\n// + - ! ~	Unary			//\r\n// *  /  % 	Arithmetic              //\r\n//////////////////////////////////////////'),(7,2,'Basic Relational Operators manipulation','This Program demonstrates the Basic Relational Operators manipulations.The relational operators are used to compare expressions.','module relational_operators(clk,a,b,c,d);\r\ninput clk;\r\noutput reg a,b,c,d;\r\n\r\nalways @(posedge clk)\r\nbegin\r\na=(5     <=  10);\r\nb=5     >=  10;\r\nc=1\'bx  <=  10;\r\nd=1\'bz  <=  10;\r\n$display (\" a = %b\", a);\r\n$display (\"b  = %b\", b);\r\n$display (\"c  = %b\", c);\r\n$display (\"d  = %b\", d);  \r\nend\r\nendmodule\r\n\r\n//////////////////////////////////////////////////\r\n//Operator 	Description			//\r\n//a < b 	a less than b			//\r\n//a > b 	a greater than b		//\r\n//a <= b 	a less than or equal to b	//\r\n//a >= b 	a greater than or equal to b	//\r\n//////////////////////////////////////////////////\r\n//\r\n//    *  The result is a scalar value (example a < b)\r\n//    * 0 if the relation is false (a is bigger then b)\r\n//    * 1 if the relation is true ( a is smaller then b)\r\n//    * x if any of the operands has unknown x bits (if a or b contains X)\r\n//\r\n//If any operand is x or z, then the result of that test is treated as false (0)\r\n'),(8,2,'Equality and Logical Operators','This program describes Case Equality,Logical Equality and logical operators and used to connect expressions.','\r\nmodule equality_logical_operators(clk,a,b,c,d,e,f,g);\r\ninput clk;\r\noutput reg a,b,c,d,e,f,g;\r\n\r\nalways @(posedge clk)\r\nbegin\r\n\r\n// Case Equality\r\na=4\'bx001 ===  4\'bx001;\r\n$display(\"a=%b\",a);\r\n\r\n// Case Inequality\r\nb=4\'bx0x1 !==  4\'bx001;\r\n$display(\"b=%b\",b);\r\n\r\n// Logical Equality\r\nc=5 == 10 ;\r\n$display(\"c=%b\",c);\r\n\r\n// Logical Inequality\r\nd=5       !=   5;\r\n$display(\"d=%b\",d);\r\n\r\n// Logical AND\r\ne=1\'b1 && 1\'b1;\r\n$display(\"e=%b\",e);\r\n\r\n// Logical OR\r\nf=1\'b1 || 1\'b0;\r\n$display(\"f=%b\",f);\r\n\r\n// Logical Negation\r\ng=! 1\'b1 ;\r\n$display(\"g=%b\",g);\r\n\r\nend \r\n\r\nendmodule\r\n\r\n// Equality  Operators\r\n//////////////////////////////////////////////////////////////////////////\r\n// Operator	Description						//\r\n// a === b 	a equal to b, including x and z (Case equality)		//\r\n// a !== b 	a not equal to b, including x and z (Case inequality)	//\r\n// a == b 	a equal to b, result may be unknown (logical equality)	//\r\n// a != b 	a not equal to b,result may be unknown(logical equality)//\r\n//////////////////////////////////////////////////////////////////////////\r\n// Logical Operators\r\n//////////////////////////////////\r\n// Operator	Description	//\r\n// ! 		logic negation	//\r\n// &&		logical and	//\r\n// ||		logical or	//\r\n//////////////////////////////////\r\n                    '),(9,2,'Manipulation of Bit-wise Operators','Bitwise operators perform a bit wise operation on two operands. They take each bit in one operand and perform the operation with the corresponding bit in the other operand. If one operand is shorter than the other, it will be extended on the left side with zeros to match the length of the longer operand.','module bitwise_operators(clk,a,b,c,d,e);\r\ninput clk;\r\noutput reg a,b,c,d,e;\r\ninitial begin\r\n\r\nend\r\n\r\nalways @(posedge clk)\r\nbegin\r\n// Bit Wise Negation\r\na=~4\'b0001;\r\n$display (\"a = %b\",a);\r\n\r\n// Bit Wise AND\r\nb=4\'b0001 &  4\'b1001;\r\n$display (\" b = %b\", b);\r\n\r\n// Bit Wise OR\r\nc=4\'b0001 |  4\'b1001;\r\n$display (\" c = %b\", c);\r\n\r\n// Bit Wise XOR\r\nd= 4\'b0001 ^  4\'b1001;\r\n$display (\" d= %b\", d);\r\n\r\n// Bit Wise XNOR\r\ne=4\'b0001 ~^ 4\'b1001;\r\n$display (\" e = %b\", e);\r\n\r\nend\r\n\r\nendmodule\r\n\r\n//////////////////////////////////////////////////\r\n// Operator        Description                        //\r\n//////////////////////////////////////////////////\r\n// ~                 negation                        //\r\n// &                 and                                //\r\n// |                 inclusive or                        //\r\n// ^                 exclusive or                        //\r\n// ^~ or ~^         exclusive nor (equivalence)        //\r\n//////////////////////////////////////////////////'),(10,2,'use of Reduction Operator','A program to state reduction operators which perform a bit-wise operation on a single operand to produce a single bit result.','module reduction_operators(clk,a,b,c,d,e,f);\r\ninput clk;\r\noutput reg a,b,c,d,e,f;\r\n\r\nalways @(posedge clk)\r\nbegin\r\n\r\n//Bit Wise AND reduction\r\na=&  4\'b1001;\r\n$display (\" a = %b\", a);\r\n\r\n// Bit Wise NAND reduction\r\nb=~& 4\'b1001;\r\n$display (\"  b= %b\", b);\r\n\r\n// Bit Wise OR reduction\r\nc=|  4\'b1001;\r\n$display (\" c = %b\", c);\r\n\r\n// Bit Wise NOR reduction\r\nd=~| 4\'b1001;\r\n$display (\" d = %b\", d);\r\n\r\n// Bit Wise XOR reduction\r\ne=^  4\'b1001;\r\n$display (\" e = %b\", e);\r\n\r\n// Bit Wise XNOR\r\nf=~^ 4\'b1001;\r\n$display (\" f = %b\", f);\r\n\r\nend\r\nendmodule\r\n\r\n\r\n\r\n//        Reduction Operators\r\n//////////////////////////////////\r\n// Operator        Description        //\r\n//////////////////////////////////\r\n// &                 and                //\r\n// ~&                 nand                //\r\n// |                 or                //\r\n// ~|                 nor                //\r\n// ^                 xor                //\r\n// ^~ or ~^         xnor                //\r\n//////////////////////////////////\r\n//\r\n//    *  Reduction operators are unary.\r\n//    * They perform a bit-wise operation on a single operand to produce a single bit result.\r\n//    * Reduction unary NAND and NOR operators operate as AND and OR respectively, but with their outputs negated.\r\n//          o Unknown bits are treated as described before.'),(11,2,'Shift Operators','The left operand is shifted by the number of bit positions given by the right operand.','module shift_operators(clk,a,b);\r\ninput clk;\r\noutput reg [3:0] a,b;\r\nalways @(posedge clk)\r\nbegin\r\n// Left Shift\r\na=4\'b1001 << 1;\r\n$display (\" a= %b\", a);\r\n// Right Shift\r\n\r\nb=4\'b1001 >> 1;\r\n$display (\" b= %b\", b);\r\n\r\nend\r\nendmodule\r\n\r\n//////////////////////////////////\r\n// Operator        Description        //\r\n//////////////////////////////////\r\n// <<                left shift        //\r\n// >>                right shift        //\r\n//////////////////////////////////                    \r\n//                \r\n//    * The left operand is shifted by the number of bit positions given by the right operand.\r\n//    * The vacated bit positions are filled with zeroes.\r\n\r\n'),(13,4,'Populating matrix elements','This program demonstrates the use of Verilog structure to input matrix elements from given matrix array','module matrix_element(clk,matin,matout);\r\ninput clk;\r\ninput [7:0] matin;//wire to input element\r\noutput reg [7:0] matout;//reg to out matrix element\r\ninteger i,j;//to fetch matrix element, i--> no of ROW, j--> No of COL \r\ninitial\r\nbegin\r\ni=1;\r\nj=1;\r\nend\r\nalways @(posedge clk)\r\nbegin\r\nmatout<=matin;\r\n$display(\"col=%d\",j);//display col\r\n$display(\"row=%d\",i);//display row\r\n$display(\"matrixout=%d \\n\",matout);//display matrix element\r\nj=j+1;//increase col\r\nif(j==4)//for 3x3 matrix j goes from 1 to n\r\nbegin\r\nj=1;\r\ni=i+1;//increase row\r\nif(i==4)//for 3x3 matrix\r\ni=1;\r\nend\r\nend\r\nendmodule\r\n                        \r\n'),(14,4,'Addition of matrices','This program demonstrates the use of Verilog syntax for addition of matrix elements with clock ','\r\n\r\n\r\n\r\nmodule matrix_element(clk,matout);\r\ninput clk;\r\noutput reg [7:0] matout;//reg to out matrix element\r\nreg [7:0] matin1 [9:1];//reg array to store matrix\r\nreg [7:0] matin2 [9:1];//reg array to store matrix elements\r\ninteger row1,col1,row2,col2,k; //to fetch matrix element,\r\ninitial\r\nbegin\r\nk=1;\r\nmatin1[1]=12;\r\nmatin1[2]=27;\r\nmatin1[3]=23;\r\n\r\nmatin1[4]=31;\r\nmatin1[5]=54;\r\nmatin1[6]=10;\r\n\r\nmatin1[7]=15;\r\nmatin1[8]=19;\r\nmatin1[9]=15;\r\n\r\nmatin2[1]=12;\r\nmatin2[2]=27;\r\nmatin2[3]=23;\r\n\r\nmatin2[4]=31;\r\nmatin2[5]=54;\r\nmatin2[6]=10;\r\n\r\nmatin2[7]=15;\r\nmatin2[8]=19;\r\nmatin2[9]=15;\r\n\r\nrow1=1;\r\ncol1=0