at','//////////////////////////////////////////////////////////////////////////////////\r\n// Company: \r\n// Engineer: \r\n// \r\n// Create Date:    18:56:34 08/03/2011 \r\n// Design Name: \r\n// Module Name:    fix_ft \r\n// Project Name: \r\n// Target Devices: \r\n// Tool versions: \r\n// Description: \r\n//\r\n// Dependencies: \r\n//\r\n// Revision: \r\n// Revision 0.01 - File Created\r\n// Additional Comments: \r\n//\r\n//////////////////////////////////////////////////////////////////////////////////\r\nmodule fix_ft(clk,ans);\r\ninput clk;\r\nreg [31:0] inp;\r\noutput reg [31:0] ans;\r\nreg [22:0] outp;\r\nreg [7:0] exponent;\r\nreg signbit;\r\n\r\ninitial \r\nbegin\r\ninp=32\'d101627986;//inp is 32 bit decimal equivalent of input floating point number 12.115(1-8-23 FORMAT)\r\nend\r\n\r\n\r\nalways @(posedge clk)\r\nbegin\r\n\r\nif (inp[31]==1)\r\nsignbit=1;\r\nelse if (inp[31]==0)\r\nsignbit=0;\r\n\r\nif (inp[30]==1)\r\nbegin\r\noutp=inp >> 7;\r\nexponent=7;\r\nend\r\n\r\nelse if (inp[29]==1)\r\nbegin\r\noutp=inp >> 6;\r\nexponent=6;\r\nend\r\n\r\nelse if (inp[28]==1)\r\nbegin\r\noutp=inp >> 5;\r\nexponent=5;\r\nend\r\n\r\nelse if (inp[27]==1)\r\nbegin\r\noutp=inp >> 4;\r\nexponent=4;\r\nend\r\n\r\nelse if (inp[26]==1)\r\nbegin\r\noutp=inp >> 3;\r\nexponent=3;\r\nend\r\n\r\nelse if (inp[25]==1)\r\nbegin\r\noutp=inp >> 2;\r\nexponent=2;\r\nend\r\n\r\nelse if (inp[24]==1)\r\nbegin\r\noutp=inp >> 1;\r\nexponent=1;\r\nend\r\n\r\nelse if (inp[23]==1)\r\nbegin\r\noutp=inp ;\r\nexponent=0;\r\nend\r\n\r\nans[31]=signbit;\r\nans[30:23]=exponent+8\'d127;\r\nans[22:0]=outp;\r\n\r\n\r\n\r\nend\r\nendmodule\r\n\r\n                        \r\n                        \r\n                        \r\n                        \r\n                        '),(24,10,'A simple program to add two variables using a function','A function in C language is a block of code that performs a specific task. It has a name and it is reusable i.e. it can be executed from as many different parts in a C Program as required. It also optionally returns a value to the calling program\r\nThis program illustrates how a function can be efficiently used to calculate the addition of two numbers','#include <stdio.h>\r\nint add (int x, int y) //declaring a funct add to cal sum of two ints\r\n{\r\nint z;		\r\nz = x + y;\r\nreturn (z);//returning sum to the main function\r\n}\r\nint main ()\r\n{\r\nint i, j, k;\r\ni = 10;\r\nj = 20;\r\nk = add(i, j);//return values is stored in k\r\nprintf (\"The value of k is %d \\n\", k);\r\n}\r\n'),(25,10,'A program to find whether a number is even or odd.','This program illustrates how a number can be checked for even and odd without using modulo function.','#include<stdio.h>\r\nint main()\r\n{\r\nint x,y,z;\r\nfloat p;\r\nx=21;\r\np=x/2;\r\nif(x==p*2)\r\nprintf(\"the number is even\");\r\nelse\r\nprintf(\"the number is odd\");\r\n}\r\n\r\n'),(26,10,'A program to find out gcd and lcm of two numbers','This program illustrates how to find out gcd and lcm of given two numbers using conditions and looping in c','#include<stdio.h>\r\nvoid main()\r\n{\r\nint x,y,i,lcm,gcd,min=0,f;\r\nx=20;y=31;\r\nif(x<y)//separating the variables as minimum and maximum\r\nmin=x;\r\nelse\r\nmin=y;\r\nfor(i=1;i<=min;i++)//to check for which integer the remainder is zero\r\n{\r\nif((x%i==0)&&(y%i==0))\r\ngcd=i;//that integer is the gcd \r\n}\r\nf=x*y;\r\nlcm=f/gcd;//a std formula to find lcm\r\nprintf(\"gcd is %d\\n\",gcd);\r\nprintf(\"lcm is %d\\n\",lcm);\r\n}\r\n\r\n\r\n\r\n'),(27,10,'A program to find addition and multiplication of two matrices','This program illustrates the program for addition and multipliation of two matrices with the help of functions and for loop','#include<stdio.h>\r\n#include<string.h>\r\nvoid add(int [3][3],int [3][3],int [3][3]);\r\nvoid mult(int [3][3],int [3][3],int [3][3]);//global declaration of the arrays\r\nint main()\r\n{\r\nint x,y,z,a[3][3],b[3][3],c[3][3],d[3][3];\r\nfor(x=0;x<3;x++)\r\n{\r\nfor(y=0;y<3;y++)\r\n{\r\na[x][y]=x*y;\r\n}\r\n}\r\nfor(x=0;x<3;x++)//loop is initialised for values of x=0,1,2\r\n{\r\nfor(y=0;y<3;y++)//loop is initialised for values of y=0,1,2 i.e for each value of x the for loop for y is initialised three times \r\n{\r\na[x][y]=x+y;\r\n}\r\n}\r\nadd(a,b,c);//addition function is called in main program\r\nmult(a,b,d);//similarly multiplication function is called in main program\r\n}\r\nvoid add(int a[3][3],int b[3][3],int c[3][3])//definition of the function-addition\r\n{\r\nint i,j;\r\nfor(i=0;i<3;i++)\r\n{\r\nfor(j=0;j<3;j++)\r\n{\r\nc[i][j]=a[i][j]+b[i][j];//element wise addition is carried out\r\nprintf(\"%d\\t\",c[i][j]);\r\n}\r\nprintf(\"\\n\");\r\n}\r\n}\r\nvoid mult(int a[3][3],int b[3][3],int d[3][3])//definition of the function-multiplication\r\n{\r\nint i,j,k;\r\nfor(k=0;k<3;k++)\r\n{\r\nfor(i=0;i<3;i++)\r\n{\r\nd[k][i]=0;\r\nfor(j=0;j<3;j++)\r\n{\r\nd[k][i]+=a[k][j]*b[j][i];//elementwise multiplication is done i.e a[3x1].b[1x3]=d[3x3]\r\n}\r\nprintf(\"%d\\t\",d[k][i]);\r\n}\r\nprintf(\"\\n\");\r\n}\r\n}\r\n\r\n'),(31,31,'','6x6 Matrix','//`timescale 1ns / 1ps\r\n//////////////////////////////////////////////////////////////////////////////////\r\n// Company: \r\n// Engineer: \r\n// \r\n// Create Date:    15:19:47 10/12/2011 \r\n// Design Name: \r\n// Module Name:    second \r\n// Project Name: \r\n// Target Devices: \r\n// Tool versions: \r\n// Description: \r\n//\r\n// Dependencies: \r\n//\r\n// Revision: \r\n// Revision 0.01 - File Created\r\n// Additional Comments: \r\n//\r\n//////////////////////////////////////////////////////////////////////////////////\r\nmodule eghtbeight(clk,ce,mout1,mout2,mout3,mout4,mout5,mout6);\r\n//,mout7,mout8);\r\n\r\ninput clk,ce;\r\noutput reg [31:0] mout1,mout2,mout3,mout4,mout5,mout6;\r\n\r\nreg [31:0] matrix1 [64:1];\r\nreg [31:0] matrix2 [64:1];\r\nreg [31:0] matrix3 [64:1];\r\n\r\nreg [63:0] pe1,pe2,pe3,pe4,pe5,pe6;\r\n\r\ninteger k,l,m,n,o,p,q;\r\ninitial\r\nbegin\r\nk=1;\r\nl=9;\r\nm=17;\r\nn=25;\r\no